            ),
            AcService(
                3,
                "Duct & Filter Cleaning",
                "data:image/jpeg;base64,",
                "4.5",
                "217"
            ),
            AcService(
                4,
                "AC Repair & Installation",
                "https://img.freepik.com/premium-photo/man-working-wall_1048944-9911943.jpg?semt=ais_hybrid&w=740&q=80",
                "4.5",
                "217"
            )
        )
        acServicesAdapter.submitList(dummyAcServices)

        val dummyOffers = listOf(
            Offer(
                1,
                "Instant\nHome Services",
                "Immediate help for home emergencies—quick, reliable, and professional.",
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7wsb5yk2HbB90Lg8Fjz7x7KBWl2Zl9mvrxQ&s",
