    private fun setupDotsIndicator() {
        val snapHelper = PagerSnapHelper()
        snapHelper.attachToRecyclerView(binding.rvReviews)

        val indicators = listOf(
            binding.indicator1,
            binding.indicator2,
            binding.indicator3,
            binding.indicator4
        )

        updateIndicators(0, indicators)

        binding.rvReviews.addOnScrollListener(object : RecyclerView.OnScrollListener() {
            override fun onScrollStateChanged(recyclerView: RecyclerView, newState: Int) {
                super.onScrollStateChanged(recyclerView, newState)
                if (newState == RecyclerView.SCROLL_STATE_IDLE) {
                    val centerView = snapHelper.findSnapView(recyclerView.layoutManager)
                    if (centerView != null) {
                        val position = recyclerView.layoutManager?.getPosition(centerView) ?: 0
                        updateIndicators(position, indicators)
                    }
                }
            }
        })
    }

    private fun updateIndicators(position: Int, indicators: List<View>) {
        val activeWidth = (24 * resources.displayMetrics.density).toInt()
        val inactiveWidth = (8 * resources.displayMetrics.density).toInt()

        for (i in indicators.indices) {
            val indicator = indicators[i]
            val params = indicator.layoutParams
            if (i == position) {
                indicator.setBackgroundResource(R.drawable.indicator_active)
                params.width = activeWidth
            } else {
                indicator.setBackgroundResource(R.drawable.indicator_inactive)
                params.width = inactiveWidth
            }
            indicator.layoutParams = params
        }
    }

    private fun observeViewModel() {
        viewModel.categoriesState.observe(viewLifecycleOwner) { result ->
            when (result) {
                is ApiResult.Loading -> {
                    binding.shimmerLayout.visibility = View.VISIBLE
                    binding.shimmerLayout.startShimmer()
                    //binding.rvServices.visibility = View.GONE
                }

                is ApiResult.Success -> {
                    binding.shimmerLayout.stopShimmer()
                    binding.shimmerLayout.visibility = View.GONE
                    binding.rvServices.visibility = View.VISIBLE

                    val services = result.data.data
                    servicesAdapter.submitList(services)
                }

                is ApiResult.Error -> {
                    binding.shimmerLayout.stopShimmer()
                    binding.shimmerLayout.visibility = View.GONE
                    Toast.makeText(context, result.message, Toast.LENGTH_SHORT).show()
                }

                is ApiResult.Empty -> {
                    binding.shimmerLayout.stopShimmer()
                    binding.shimmerLayout.visibility = View.GONE
                }
            }
        }

        viewModel.rootState.observe(viewLifecycleOwner) { result ->
            when (result) {
                is ApiResult.Success -> {
                    val categories = result.data.data
                    categoriesAdapter.submitList(categories)
                }

                is ApiResult.Error -> {
                    Log.e("HomeFragment", "Categories Error: ${result.message}")
                }

                else -> {}
            }
        }

        lifecycleScope.launch {
            viewLifecycleOwner.repeatOnLifecycle(Lifecycle.State.STARTED) {
                viewModel.uiEvent.collect { message ->
                    showSnackBar(message)
                }
            }
        }
    }

    fun showSnackBar(message: String) {
        val snackBar = Snackbar.make(binding.root, message, Snackbar.LENGTH_SHORT)
        if (message.contains("No Internet")) {
            snackBar.setBackgroundTint(Color.RED)
        } else {
            snackBar.setBackgroundTint(Color.GREEN)
        }
        snackBar.show()
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}
